import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ChallengeDashboard.css";

const ChallengeDashboard = () => {
  const [challenges, setChallenges] = useState([]);
  const [completed, setCompleted] = useState([]);

  useEffect(() => {
    fetchChallenges();
  }, []);

  const fetchChallenges = async () => {
    try {
      const response = await axios.get("http://localhost:5000/challenges", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      setChallenges(response.data);
    } catch (error) {
      console.error("Error fetching challenges:", error);
    }
  };

  const handleComplete = async (challengeId) => {
    try {
      const response = await axios.post(
        `http://localhost:5000/complete-challenge/${challengeId}`,
        {},
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      // Update completed challenges
      const newCompleted = [...completed];
      newCompleted[challengeId - 1] = true;
      setCompleted(newCompleted);

      // Trigger parent component update (if needed)
      if (response.data.score) {
        // You can use a context or prop function to update the dashboard
        window.dispatchEvent(
          new CustomEvent("scoreUpdate", {
            detail: response.data,
          })
        );
      }
    } catch (error) {
      console.error("Error completing challenge:", error);
    }
  };

  return (
    <div className="dashboard">
      <h2>CTF Challenge Dashboard</h2>
      <div className="challenge-list">
        {challenges.map((challenge, index) => (
          <div className="challenge-card" key={challenge.id}>
            <h3>{challenge.title}</h3>
            <p>{challenge.description}</p>
            <span
              className={`difficulty ${challenge.difficulty.toLowerCase()}`}
            >
              Difficulty: {challenge.difficulty}
            </span>
            <span className="status">
              Status: {completed[index] ? "Completed" : challenge.status}
            </span>
            <div className="progress-bar">
              <div
                className="progress"
                style={{ width: completed[index] ? "100%" : "0%" }}
              ></div>
            </div>
            <button
              onClick={() => handleComplete(challenge.id)}
              disabled={completed[index]}
            >
              {completed[index] ? "Challenge Completed" : "Start Challenge"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ChallengeDashboard;
