import React, { useState, useRef } from "react";
import "./Signup.css";
import userIdIcon from "./images/userIdIcon.png";

const API_URL = "https://ctf-backend-03il.onrender.com";

const Signup = () => {
  const [userId, setUserId] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const [image, setImage] = useState(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // Start camera
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
    } catch (err) {
      setError("Error accessing camera");
    }
  };

  // Capture image from camera
  const captureImage = () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    canvas.getContext("2d").drawImage(video, 0, 0, canvas.width, canvas.height);
    const imageData = canvas.toDataURL("image/png");
    setImage(imageData); // Set image as base64
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!userId || !username || !email || !password || !confirmPassword || !image) {
      setError("All fields, including ID photo, are required");
      return;
    }

    if (password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError("Please enter a valid email address");
      return;
    }

    setError("");
    try {
      const response = await fetch(`${API_URL}/signup`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          userId,
          username,
          email,
          password,
          image, // Send image data
        }),
      });

      const data = await response.json();

      if (response.ok) {
        console.log("Signup successful:", data);
        if (data.redirect) {
          window.location.href = data.redirect;
        }
      } else {
        setError(data.message || "Signup failed");
      }
    } catch (err) {
      setError("Network error occurred");
      console.error("Signup error:", err);
    }
  };

  return (
    <div className="signup-container">
      <h2>Create a New Account</h2>
      <form onSubmit={handleSubmit}>
        {error && <p className="error-message">{error}</p>}

        {/* Other form fields */}
        <div className="form-group">
          <label htmlFor="userId">User ID</label>
          <div className="input-with-icon">
            <img src={userIdIcon} alt="User ID Icon" className="user-id-icon" />
            <input
              type="text"
              id="userId"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
              required
            />
          </div>
        </div>

        {/* Other input fields */}
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="confirmPassword">Confirm Password</label>
          <input
            type="password"
            id="confirmPassword"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
        </div>

        {/* Camera and capture functionality */}
        <div className="form-group">
          <label>ID Card Photo</label>
          <div className="camera-container">
            <video ref={videoRef} autoPlay className="video-preview"></video>
            <button type="button" onClick={startCamera} className="camera-button">
              Start Camera
            </button>
            <button type="button" onClick={captureImage} className="capture-button">
              Capture ID
            </button>
          </div>
          <canvas ref={canvasRef} style={{ display: "none" }}></canvas>
          {image && (
            <div className="image-preview">
              <img src={image} alt="Captured ID" />
            </div>
          )}
        </div>

        <button type="submit" className="submit-button">
          Sign Up
        </button>
      </form>
    </div>
  );
};

export default Signup;
